import React, { useEffect, useState } from "react";
import './Hertl.css';
import playerList from './scraping/nhl_players.json'

function Hertl(props) {
    // state variables
    const [searching, setSearching] = useState(false);
    const [filtered, setFiltered] = useState([]);
    const [guesses, setGuesses] = useState([]);
    const [oneGuess, setOneGuess] = useState(false);

    // generate correct player
    const correctPlayer = getRandomPlayer();

    // update doc title
    useEffect(() => {
        document.title = props.title;
    }, [props.title]);

    // generates random player from player list
    function getRandomPlayer() {
        return playerList[Math.floor(Math.random() * playerList.length)]
    }

    // filter player list based on search inputted by user
    function search(event) {
        setFiltered([]);
        const val = event.target.value;
        if (val.length >= 3) {
            setSearching(true);
            playerList.forEach((player) => {
                const name = player.name;
                if (name.toLowerCase().includes(val.toLowerCase())) {
                    setFiltered((old) => [...old, player]);
                }
            });
        }
    }

    // make a guess via selected search
    function guess(event) {
        setSearching(false);
        setFiltered([]);
        document.getElementById("search-field").value = "";

        let guessed = checkGuess(getFilteredPlayer(event.target.id));
        console.log(guessed);
        setGuesses((old) => [...old, guessed]);
        if (!oneGuess) {
            setOneGuess(true);
        }
    }

    // gets player object for searched player
    function getFilteredPlayer(id) {
        let selected;
        filtered.forEach((player) => {
            if (player.id === parseInt(id)) {
                selected = player;
            }
        });
        return selected;
    }

    // checks guessed player object against the correct player
    function checkGuess(player) {
        let updated = player;
        return updated;
    }

    return (
        <main>
            <div id="container">
                <input id="search-field" onChange={search} />
                {searching &&
                    (<div id="searchResults">
                        <ul>
                            {
                                filtered.map((player) => {
                                    return (
                                        <li key={player.id} id={player.id} onClick={guess}>{player.name}</li>
                                    )
                                })
                            }
                        </ul>
                    </div>)
                }
                {oneGuess &&
                (<div id="guesses-table">
                    <table>
                        <thead>
                            <tr>
                                <th>Name</th>
                                <th>Team</th>
                                <th>Conference</th>
                                <th>Division</th>
                                <th>Nationality</th>
                                <th>Position</th>
                                <th>Age</th>
                                <th>Height</th>
                                <th>Weight</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                guesses.map((guess) => {
                                    return (
                                        <tr key={guess.id}>
                                            <td>{guess.name}</td>
                                            <td>{guess.team}</td>
                                            <td>{guess.conference}</td>
                                            <td>{guess.division}</td>
                                            <td>{guess.nationality}</td>
                                            <td>{guess.position}</td>
                                            <td>{guess.currentAge}</td>
                                            <td>{guess.height}</td>
                                            <td>{guess.weight}</td>
                                        </tr>
                                    )
                                })
                            }
                        </tbody>
                    </table>
                </div>)
                }
            </div>
        </main>
    );
}

export default Hertl;
